import axios from 'axios'

const currentURL = window.location.href
var baseURL = "https://eis-hierarchy-status-service-dev.cfcdcinternaltest.kroger.com"

if (currentURL.toString().includes("localhost")) {
    window.$currentEnv = "local"
}

if (currentURL.toString().includes("-feature")) {
    window.$currentEnv = "feature"
}

if (currentURL.toString().includes("-dev")) {
    window.$currentEnv = "dev"
}

if (currentURL.toString().includes("-test")) {
    window.$currentEnv = "test"
    baseURL = "https://eis-hierarchy-status-service-test.cfcdcinternaltest.kroger.com"
}

if (currentURL.toString().includes("-stage")) {
    window.$currentEnv = "stage"
    baseURL = "https://eis-hierarchy-status-service-stage.cfcdcinternaltest.kroger.com"
}

if (currentURL.toString().includes("-prod")) {
    window.$currentEnv = "prod"
    baseURL = "https://eis-hierarchy-status-service-prod.cfcdcinternal.kroger.com"
}

console.log("currentEnv axios:", window.$currentEnv)

export default axios.create({
    baseURL: baseURL
});
