import cx from 'classnames'
import PropTypes from 'prop-types'
import React from 'react'

export const sizeOpts = ['l', 'm', 's', 'xs']

const Text = ({ tag: Tag, bold, size, ...spanProps }) => (
  <Tag
    {...spanProps}
    className={cx(
      `kds-Text--${size}`,
      { 'kds-Text--bold': bold },
      spanProps.className
    )}
  />
)

Text.propTypes = {
  size: PropTypes.oneOf(sizeOpts),
  bold: PropTypes.bool,
  /** Customize the text element's tag */
  tag: PropTypes.string
}

Text.defaultProps = {
  size: 'm',
  bold: false,
  tag: 'span'
}

export default React.memo(Text)
