export const forceArray = val => (Array.isArray(val) ? val : [val])

export const getUniqueValuesByKey = (arr, key) => {
  return Object.keys(
    arr.reduce((acc, item) => {
      if (item[key]) {
        acc[item[key]] = true
      }
      return acc
    }, {})
  )
}
