import React from 'react'
import PropTypes from 'prop-types'
import Breadcrumbs from './Breadcrumbs'
import SchemaForBreadcrumbs from './SchemaForBreadcrumbs'

/**
 * Breadcrumbs are used to show the User where they are inside the site's hierarchy and give them the ability to move to a higher level.
 */

const BreadcrumbsWithSchema = props => {
  const { breadcrumbs } = props
  return (
    <>
      <Breadcrumbs {...props} />
      <SchemaForBreadcrumbs breadcrumbs={breadcrumbs} />
    </>
  )
}

BreadcrumbsWithSchema.defaultProps = {
  breadcrumbs: [],
  inverse: false
}

BreadcrumbsWithSchema.propTypes = {
  /** An array of objects with the title and url for each breadcrumb */
  breadcrumbs: PropTypes.arrayOf(
    PropTypes.shape({
      title: PropTypes.string,
      url: PropTypes.string
    })
  ),
  /** Set to true if the breadcrumb is placed on the brand surface background. */
  inverse: PropTypes.bool,
  /** Pass utility classes to the container element */
  className: PropTypes.string
}

export default BreadcrumbsWithSchema
