import React from 'react'
import cx from 'classnames'
import PropTypes from 'prop-types'

/**
 * A card provides a defined space within the page hierarchy to hold related content and the actions tied to that content.
 *
 * > **Note:** `<Card />` renders a `div` with all props spread on, like so: `<div {...props} />`.
 * So you can pass any relevant props such as:  `id`, `className`, aria attributes, etc.
 */
const Card = ({ tag: Tag, ...props }) => (
  <Tag {...props} className={cx('kds-Card', props.className)} />
)

Card.defaultProps = {
  tag: 'div'
}

Card.propTypes = {
  /** Overrides the card's tag */
  tag: PropTypes.string
}

export default Card
