import React, { memo } from 'react'
import cx from 'classnames'

export const PaginationDots = memo(({ numberOfSections, currentSection }) => {
  if (numberOfSections < 2 || numberOfSections > 10) {
    return null
  }

  return (
    <div className="kds-Carousel-indicators">
      {Array.from({ length: numberOfSections }, (v, i) => {
        const sectionNumber = i + 1
        return (
          <div
            key={sectionNumber}
            className={cx('kds-Carousel-indicator', {
              'kds-Carousel-indicator--active': currentSection === sectionNumber
            })}
          />
        )
      })}
    </div>
  )
})
