import React from 'react'
import Checkbox from './Checkbox'
import { render } from 'react-testing-library'
import { checkboxContract } from 'kds-web-e-commerce-theme'

describe('<Checkbox />', () => {
  it('renders a checkbox with proper classes', () => {
    const { container } = render(<Checkbox className="extraClass" />)

    expect(container.firstChild).toMatchInlineSnapshot(`
<input
  class="kds-Checkbox extraClass"
  type="checkbox"
/>
`)
    expect(checkboxContract.validateHTML(container.innerHTML)).toBeTruthy()
  })

  it('passes arbitrary props down to checkbox element', () => {
    const { container } = render(
      <Checkbox
        name="checkbox"
        value="selected"
        checked
        role="switch"
        onChange={() => {}}
      />
    )

    expect(container.firstChild).toMatchInlineSnapshot(`
<input
  checked=""
  class="kds-Checkbox"
  name="checkbox"
  role="switch"
  type="checkbox"
  value="selected"
/>
`)
    expect(checkboxContract.validateHTML(container.innerHTML)).toBeTruthy()
  })
})
