import PropTypes from 'prop-types'
import React from 'react'
import { generateUniqueString } from '../../../utils/generationUtils'
import Heading from '../../Heading/Heading'

/**
 * `FormGroup` will visually group multiple [`Form`](./form) fields in an accessible manner.
 */
const FormGroup = ({ label, children }) => {
  const id = `${generateUniqueString()}-formGroup`

  return (
    <div
      role="group"
      aria-labelledby={id}
      className="kds-Form--field kds-FormGroup"
    >
      <Heading size="s" id={id} className="mb-8">
        {label}
      </Heading>
      {children}
    </div>
  )
}

FormGroup.propTypes = {
  /** The label shown above the group of inputs */
  label: PropTypes.string.isRequired
}

export default FormGroup
