import React from 'react'
import { render } from 'react-testing-library'
import FormGroup from './FormGroup'

jest.mock('../../../utils/generationUtils', () => ({
  generateUniqueString: () => '1234'
}))

describe('FormGroup', () => {
  it('renders an aria group with label and contents', () => {
    const { getByLabelText } = render(
      <FormGroup label="A Label">Contents</FormGroup>
    )

    expect(getByLabelText('A Label')).toHaveAttribute('role', 'group')
    expect(getByLabelText('A Label')).toHaveTextContent('Contents')
  })
})
