import React from 'react'
import TextArea from '../../TextArea/TextArea'
import FormField from '../FormField/FormField'

/**
 * `FormTextArea` lets users enter more text for submission via a form.
 *
 * > **Note:** `<FormTextArea />` renders a `<FormField />` with most props spread on.
 * For more details, see the [FormField](./formfield) and [TextArea](./textarea) components.
 *
 * It comes with accessible form features:
 *
 * - label and description
 * - validation messaging
 * - aria attributes
 *
 * **Requires *custom-event* polyfill for custom validations to work in IE11** [See Polyfills](./polyfills)
 */

const FormTextArea = props => <FormField {...props} as={TextArea} />

export default FormTextArea
