import React from 'react'
import { render } from 'react-testing-library'
import JsonLd from './JsonLd'
import { generateData } from '../Breadcrumbs/SchemaForBreadcrumbs'

describe('JsonLd component', () => {
  it('sucessfully builds JSON-LD when called', () => {
    const breadcrumbs = [
      { title: 'Home', url: '/' },
      { title: 'Baby', url: '/pl/baby/10' },
      {
        title: 'Baby & Toddler Food, Snacks & Beverages',
        url: '/pl/baby-toddler-food-snacks-beverages/10090'
      },
      {
        title: 'Baby Food Pouches',
        url: '/pl/baby-food-pouches/1009000772'
      }
    ]

    const { container } = render(<JsonLd data={generateData(breadcrumbs)} />)

    expect(container).toMatchSnapshot()
  })
})
