import React from 'react'
import PropTypes from 'prop-types'
import cx from 'classnames'

/**
 * Loading Indicators communicate the status of an activity happening in the background.
 * Use to provide the User feedback as they navigate and perform complex actions.
 *
 * >**Note:** `<LoadingBar>` renders a `<progress>` with props spread on, like `<progress {...props} />`.
 * So you can pass [its native props](https://developer.mozilla.org/en-US/docs/Web/API/HTMLProgressElement): `id`, `className`, `aria-label`, etc.
 */
const LoadingBar = ({ ...props }) => (
  <progress
    aria-label="Loadingâ€¦"
    {...props}
    className={cx('kds-LoadingBar', props.className)}
  />
)

LoadingBar.propTypes = {
  /** The total amount of progress to be made. */
  max: PropTypes.number,
  /** Current completion amount out of `max`. */
  value: PropTypes.number
}

export default LoadingBar
