import React from 'react'
import LoadingBar from './_LoadingBar'
import { render } from 'react-testing-library'

describe('<LoadingBar>', () => {
  it('renders with added classes', () => {
    const { container } = render(<LoadingBar className="extraClass" />)

    expect(container.firstChild).toHaveClass('kds-LoadingBar extraClass')
  })

  it('passes arbitrary props to the <progress> element', () => {
    const { container } = render(<LoadingBar data-test="sup" />)

    expect(container.querySelector('progress')).toHaveAttribute(
      'data-test',
      'sup'
    )
  })
})
