export const deprecationWarning = (message, options = {}) => {
  const isDev = process.env.NODE_ENV === 'development'
  const {
    logLevel = 0, // 0 => 'warn', 1 => 'error'
    removeInDev = false
  } = options
  const log = console[logLevel === 1 ? 'error' : 'warn']

  if (isDev) {
    if (removeInDev) {
      throw Error(`[Deprecation] ${message}`)
    } else {
      log(`[Deprecation] ${message}`)
    }
  }
}
