import { ofEnterKey } from './keyboardUtils'

describe('keyboardUtils', () => {
  it('ofEnterKey calls callback if keycode is correct', () => {
    const fn = jest.fn()
    ofEnterKey(fn)({ key: 'Enter' })
    expect(fn).toBeCalled()

    const fn2 = jest.fn()
    ofEnterKey(fn2)({ key: 'Other' })
    expect(fn2).not.toBeCalled()
  })
})
