module.exports.camelize = str => {
  return (
    str &&
    str.toString().replace(/(?:^\w|[A-Z]|\b\w|\s+)/g, function (match, index) {
      if (+match === 0) return '' // or if (/\s+/.test(match)) for white spaces
      return index === 0 ? match.toLowerCase() : match.toUpperCase()
    })
  )
}

module.exports.toTitleCase = str =>
  str &&
  str
    .replace(
      /([a-z0-9])([a-z0-9]*)/gi,
      (m, p1, p2) => `${p1.toUpperCase()}${p2.toLowerCase()}`
    )
    .replace(/[\s-_]/g, '')

module.exports.isValidNonEmptyString = str =>
  str && typeof str === 'string' && str.length && str.trim().length
