import kebabCase from "just-kebab-case";
/**
 * Convenience utility to allow internal developers to convert style objects into strings.
 * Used when you want to set a style object onto an `inner-style` attribute.
 * @param styleObj Style object represented with camel cased keys.
 */
export default function (styleObj) {
    const dashedStyles = Object.keys(styleObj).reduce((acc, key) => {
        const dashedKey = kebabCase(key);
        acc[dashedKey] = styleObj[key];
        return acc;
    }, {});
    return JSON.stringify(dashedStyles)
        .replace(/('|"|{|})/g, "")
        .replace(",", ";");
}
